import React, {Component} from 'react';
import './App.css';


function Board(props){
  return(
    <div className='square' key={props.id} id={props.id} value={props.value} onClick={props.onClick()}></div>
  )
}

class App extends Component {

constructor(props){
  super(props)
  this.state = {
    squares: [
      { id: 1,
value: null
      },
      {
        id: 2,
        value: null
      },
      {
        id: 3,
        value: null
      },
      {
        id: 4,
        value: null
      },
      {
        id: 5,
        value: null
      },
      {
        id: 6,
        value: null
      },
      {
        id: 7,
        value: null
      },
      {
        id: 8,
        value: null
      },
      {
        id: 9,
        value: null
      } 
    ]
  }
}




