  }

renderSquare(i){
  return <Board id={i} value={i} onClick={() => this.selectedBox(i)} />
}



render(){

  const { id, value } = this.state.squares;


  return (
    <div className="App">
      <header className="App-header">
        <div>
          <div className='board-row'>
            {this.renderSquare(1)}
            {this.renderSquare(2)}
            {this.renderSquare(3)}
          </div>
          <div className='board-row'>
            {this.renderSquare(4)}
            {this.renderSquare(5)}
            {this.renderSquare(6)}
          </div>
          <div className='board-row'>
            {this.renderSquare(7)}
            {this.renderSquare(8)}
            {this.renderSquare(9)}
          </div>
          <div>
            {this.state.squares.map(square => <Board id={id} value={value}/>)}  
          </div>
        </div>
      </header>
    </div>
  );
}

}



export default App;
