import React, { Component } from 'react'
import './App.css';

export class App extends Component {

  turns = ['X','O','X','O','X','O','X','O','X'];

  constructor(props){
    super(props);
    this.state = [
      {square1: null},
      {square2: null},
      {square3: null},
      {square4: null},
      {square5: null},
      {square6: null},
      {square7: null},
      {square8: null},
      {square9: null}
    ]
  }



  selectSquare(square){

    this.setState({
      square: this.turns.pop()
    });
    console.log(this.turns)
  }



  render() {
    return (
      <div className='App'>
        <div className='App-header'>
          <div>
            <button className='square' onClick={()=> this.selectSquare(0)}>{this.state.square1}</button>
            <button className='square' onClick={()=> this.selectSquare(this.props)}>{this.state.square2}</button>
          </div>
        </div>
      </div>
    )
  }
}

export default App;
