import React, {Component} from 'react';
import './App.css';

function Board(props) {
  let table = []
  let idNumber;

  for(let i=1; i<=3; i++){
    let row = []
    for(let j=1; j<=3; j++){
      idNumber++
    row.push(<div 
      className='square' 
      key={idNumber} 
      onClick={props.onClick}
      >{props.value}</div>)
    };

    table.push(<div className='board-row'>{row}</div>)
  }
  return table;
};


class App extends Component {
  state = {
    squares: [
      {
        id: 1,
        value: null
      },
      {
        id: 2,
        value: null
      },
      {
        id: 3,
        value: null
      },
      {
        id: 4,
        value: null
      },
      {
        id: 5,
        value: null
      },
      {
        id: 6,
        value: null
      },
      {
        id: 7,
        value: null
      },
      {
        id: 8,
        value: null
      },
      {
        id: 9,
        value: null
      } 
    ]
  }

  selectedBox(event){
    console.log(event.target)
  }

render(){

  return (
    <div className="App">
      <header className="App-header">
      </header>
      <div onClick={(e) => this.selectedBox(e)}>
        <Board/>
      </div>
    </div>
  );
}

}



export default App;
